import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
import { Link, useParams } from "react-router-dom";
import { BulletList } from "react-content-loader";
import BlogAPI from "../../API/BlogAPI";

export default function FeaturedArticle() {
  // SCROLL TO TOP
  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  const MyBulletListLoader = () => <BulletList />;

  const [error, setError] = useState(false);

  const [blog, setBlog] = useState({});
  const { slug } = useParams();

  const [featuredBlog, setFeaturedBlog] = useState([]);

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Loader
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1000);

    // FeaturedAticle
    const fetchData = async () => {
      try {
        const res = await BlogAPI.get(`/api/blog/featured`);
        // If there are no featured posts
        if (res.data && res.data.length > 0) {
          setFeaturedBlog(res.data[0]);
        }
      } catch (err) {}
    };
    fetchData();
  }, [slug]);

  const capitalLetter = (word) => {
    if (word) return word.charAt(0).toUpperCase() + word.slice(1);
    return "";
  };

  let getTime = (blog) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(blog).toLocaleDateString("en", options);
  };

  return (
    <>
      <div
        className="bg-image p-5 text-center shadow-1-strong rounded mb-5 text-white"
        style={{
          backgroundImage: `url("https://mdbcdn.b-cdn.net/img/new/slides/003.webp")`,
        }}
      >
        <h1 className="mb-3 h2">
          {loading ? (
            <MyBulletListLoader />
          ) : featuredBlog.length === 0 ? (
            <h1 className="mb-3 h2">Stay tuned for my upcoming featured articles!</h1>
          ) : (
            <Link
              to={`/articles/${featuredBlog.slug}`}
              onClick={scrollToTop}
              className="text-decoration-underline text-white fw-bold text-capitalize display-3"
            >
              {featuredBlog.title}
            </Link>
          )}
          </h1>
        <button type="button" className="btn btn-primary">
          <h6 className="fw-bold"><Link to={`/articles/${featuredBlog.slug}`} className="text-white fs-2">Explore Now</Link></h6>
        </button>
      </div>
    </>
  );
}
