import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { BulletList } from "react-content-loader";
import BlogAPI from "../../API/BlogAPI";

export default function RecentArticles() {
  // SCROLL TO TOP
  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  const MyBulletListLoader = () => <BulletList />;

  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true);

  const capitalLetter = (word) => {
    return word ? `${word.charAt(0).toUpperCase()}${word.slice(1)}` : "";
  };

  useEffect(() => {
    // Loader
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2000);

    const fetchBlogs = async () => {
      try {
        const res = await BlogAPI.get(`/api/blog`);
        setBlogs(res.data);
      } catch (err) {
        // Handle error
      }
    };
    fetchBlogs();
  }, []);

  const getBlogs = () => {
    return blogs.slice(0, 4).map((blogPost, index) => (
      <>
        <div className="col" key={index}>
          <div className="card h-100">
            <img
              src={blogPost.image}
              className="card-img-top"
              alt={blogPost.title}
              style={{ height: "50vh" }}
            />
            <div className="card-body">
              <p className="card-text">
                {loading ? (
                  <MyBulletListLoader />
                ) : (
                  <Link
                    to={`/article/${blogPost.slug}`}
                    onClick={scrollToTop}
                    className="text-decoration-none"
                  >
                    <h5 className="card-title">
                      {capitalLetter(blogPost.title)}
                    </h5>
                  </Link>
                )}
              </p>
            </div>
            <div className="card-footer">
              <small className="text-muted">Last updated 3 mins ago</small>
            </div>
          </div>
        </div>
      </>
    ));
  };
  return <div className="row row-cols-1 row-cols-md-2 g-4">{getBlogs()}</div>;
}
