import React, { useEffect, useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import BlogFeeds from "./components/blog/BlogFeeds";
import ThankYou from "./components/thankYou/ThankYou";
import NavBar from "./components/navBar/NavBar";
import { lazy, Suspense } from "react";
import { ErrorBoundary } from "react-error-boundary";
import Home from "./components/homePage/Home";
import NotFound from "./components/notFound/NotFound";
import ErrorFallback from "./components/error/ErrorFallback";
import Footer from "./components/footer/Footer";
import Newsletter from "./components/newsLetter/Newsletter";
import SubThankYou from "./components/newsLetter/SubThankYou";

const HireMe = lazy(() => import("./components/hireMe/HireMe"));
const BlogDetails = lazy(() => import("./components/blog/BlogDetails"));
const AboutMe = lazy(() => import("./components/aboutMe/AboutMe"));

function App() {
  const navigate = useNavigate();
  const [isFormSubmitted, setFormSubmitted] = useState(false);

  useEffect(() => {
    if (!isFormSubmitted) {
      const currentPath = window.location.pathname;
      if (
        currentPath === "/thank-you" ||
        currentPath === "/404" ||
        currentPath === "/thank-you-for-subscribing"
      ) {
        navigate("/"); // Redirect to the home page if the form is not submitted
      }
    }
  }, [isFormSubmitted, navigate]);

  const handleFormSubmission = (submitted) => {
    setFormSubmitted(submitted);
  };

  return (
    <>
      <NavBar />
      <Routes>
        <Route
          exact
          // path="/articles/:slug"
          path="/articles/:slug"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>
                <>
                  <BlogDetails />
                  {/* <Footer /> */}
                </>
              </Suspense>
            </ErrorBoundary>
          }
        />
        <Route
          exact
          path="/me"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>
                <>
                  <AboutMe />
                  {/* <Footer /> */}
                </>
              </Suspense>
            </ErrorBoundary>
          }
        />

        <Route
          exact
          path="/articles"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>
                <BlogFeeds />
              </Suspense>
            </ErrorBoundary>
          }
        />

        <Route
          exact
          path="/newsletter"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>
                <Newsletter />
              </Suspense>
            </ErrorBoundary>
          }
        />

        <Route
          exact
          path="/"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>
                <>
                  <Home />
                  {/* <Footer /> */}
                </>
              </Suspense>
            </ErrorBoundary>
          }
        />

        <Route
          exact
          path="/hire-me"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>
                <HireMe onFormSubmit={handleFormSubmission} />
              </Suspense>
            </ErrorBoundary>
          }
        />

        <Route
          exact
          path="/thank-you"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>
                {isFormSubmitted ? (
                  <ThankYou />
                ) : (
                  <NotFound /> // Render a not-found page instead of the thank-you page if the form is not submitted
                )}
              </Suspense>
            </ErrorBoundary>
          }
        />

        <Route
          path="/404"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>{isFormSubmitted ? <NotFound /> : <Home />}</Suspense>
            </ErrorBoundary>
          }
        />

        <Route
          path="/thank-you-for-subscribing"
          element={
            <ErrorBoundary
              onReset={() => navigate("/")}
              fallbackRender={({ error, resetErrorBoundary }) => (
                <ErrorFallback
                  error={error}
                  resetErrorBoundary={resetErrorBoundary}
                />
              )}
            >
              <Suspense>
                {isFormSubmitted ? <SubThankYou /> : <NotFound />}
              </Suspense>
            </ErrorBoundary>
          }
        />

        {/* Other routes */}
      </Routes>
      <Footer />
    </>
  );
}

export default App;
