import React, { useRef, useState } from "react";
import emailjs from "@emailjs/browser";
import { useNavigate } from "react-router-dom";
import { Helmet } from "react-helmet";
import ThankYou from "../thankYou/ThankYou";

export default function HireMe() {
  const navigate = useNavigate();
  const form = useRef();
  const [isFormSubmitted, setFormSubmitted] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();
    emailjs
      .sendForm(
        "service_pwvmojf",
        "template_ygg6k2c",
        form.current,
        "user_qGuGQyWW4uKSaHcStVUxO"
      )
      .then(
        (result) => {
          console.log(result.text);
          setFormSubmitted(true); // Set the form submission status to true
          navigate("/thank-you"); // Redirect to the thank-you page
        },
        (error) => {
          console.log(error.text);
        }
      );
    e.target.reset();
  };

  const handleRedirect = () => {
    if (isFormSubmitted) {
      navigate("/thank-you"); // Redirect to the thank-you page
    } else {
      navigate("/"); // Redirect to the home page
    }
  };

  if (isFormSubmitted) {
    return (
      <>
        <Helmet>
          <title>Dennis Mbugua - Thank You</title>
          {/* Add other meta tags for the thank-you page */}
        </Helmet>
        {/* Render your thank-you page JSX here */}
        <ThankYou/>
      </>
    );
  }

  return (
    <>
      <div className="container my-5">
        <div className="row justify-content-center">
          <div className="col-lg-9 text-dark fw-bold">
            <h1 className="mt-5 text-center fw-bold">
              The Best Thing For You is Me! &#128522;
            </h1>
            <hr />
            <h3 className="mb-3 text-dark ">
              With me youâ€™re not going to get an off-the-shelf, run-of-the mill
              type of project and results. I work with you & your team to
              understand the direction the company is heading with steadfast
              readiness to offer sincere service. I have a strong pristine
              focus, in my pursuit in ensuring I produce marvelous and
              exceptional work &#128640; with a lot of love &#10084;&#65039; for
              my clients.
            </h3>
            <hr />
            <h3 className="text-center text-dark fw-bold fs-3">
              Am the guy to contact when you're tired of playing small{" "}
            </h3>

            <hr className="mb-5" />
            <form ref={form} onSubmit={sendEmail}>
              <div className="row g-3">
                <div className="col-md-6 text-center">
                  <label htmlFor="firstname" className="form-label">
                    First Name
                  </label>
                  <input
                    style={{ textAlign: "center" }}
                    type="text"
                    className="form-control form-control-lg"
                    id="firstname"
                    name="firstname"
                    placeholder="Your First Name"
                    required
                  />
                </div>
                <div className="col-md-6 text-center">
                  <label htmlFor="lastname" className="form-label">
                    Last Name
                  </label>
                  <input
                    style={{ textAlign: "center" }}
                    type="text"
                    className="form-control form-control-lg"
                    id="lastname"
                    name="lastname"
                    placeholder="Your Last Name"
                    required
                  />
                </div>
                <div className="col-md-6 text-center">
                  <label htmlFor="email" className="form-label">
                    Your Email
                  </label>
                  <input
                    style={{ textAlign: "center" }}
                    type="email"
                    className="form-control form-control-lg"
                    id="email"
                    name="email"
                    placeholder="Your Best Email"
                    required
                  />
                </div>
                <div className="col-md-6 text-center">
                  <label htmlFor="subject" className="form-label">
                    Subject
                  </label>
                  <input
                    style={{ textAlign: "center" }}
                    type="text"
                    className="form-control form-control-lg"
                    id="subject"
                    name="subject"
                    placeholder="Your Subject Matter"
                  />
                </div>
                <div className="col-12 text-center">
                  <label htmlFor="message" className="form-label">
                    Your Message
                  </label>
                  <textarea
                    className="form-control"
                    id="message"
                    name="message"
                    rows="5"
                    placeholder="Write Your Message in Details"
                    required
                  ></textarea>
                </div>
                <div className="col-12 mb-5">
                  <div className="row">
                    <div className="col-md-12">
                      <button
                        type="submit"
                        className="btn btn-block w-100 h-100"
                        style={{ backgroundColor: "#007BFF" }}
                        disabled={isFormSubmitted} // Disable the button after form submission
                        onClick={handleRedirect}
                      >
                        <h5 className="fw-bold text-white">
                          Reach out and let's connect!
                        </h5>
                      </button>
                    </div>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </>
  );
}

