import React, { useState, useEffect } from "react";
// import { HelmetProvider } from "react-helmet-async";
import { Helmet } from "react-helmet";
import { Link, useParams, useNavigate } from "react-router-dom";
import Me from "./me.png";
// import { List } from "react-content-loader";
import { BulletList } from "react-content-loader";
import {
  RedditShareButton,
  RedditIcon,
  TelegramShareButton,
  TelegramIcon,
  TwitterShareButton,
  TwitterIcon,
  EmailShareButton,
  EmailIcon,
  WhatsappIcon,
  WhatsappShareButton,
} from "react-share";
import BlogAPI from "../../API/BlogAPI";
import RecentArticles from "./recentArticles";

export default function BlogDetails() {
  // SCROLL TO TOP
  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  const MyBulletListLoader = () => <BulletList />;

  // let url = slug;

  const navigate = useNavigate();

  const [blog, setBlog] = useState({});
  const { slug } = useParams();

  const [featuredBlog, setFeaturedBlog] = useState([]);

  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Loader
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1000);

    // FeaturedAticle
    const fetchData = async () => {
      try {
        const res = await BlogAPI.get(`/api/blog/featured`);
        setFeaturedBlog(res.data[0]);
      } catch (err) {}
    };
    fetchData();

    // BlogPost
    const fetchBlog = async () => {
      try {
        const response = await BlogAPI.get(`/api/blog/${slug}`); // <-- passed to API URL
        setBlog(response.data);
      } catch (err) {
        if (err) {
          navigate("/404");
        }
      }
    };
    fetchBlog();

    // fetch(fetchBlog)
    //   .then((response) => {
    //     if (response.status === 404){
    //       navigate('/404')
    //     }
    //     return response.json
    //   })
  }, [slug]);

  const createBlog = () => {
    return { __html: blog.body };
  };
  const capitalLetter = (word) => {
    if (word) return word.charAt(0).toUpperCase() + word.slice(1);
    return "";
  };

  const shareUrl = `https://www.dennismbugua.co.ke/${slug}`;
  // const shareUrl = window.location.href;

  let getTime = (blog) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(blog).toLocaleDateString("en", options);
  };

  // const helmetContext = {};

  return (
    <>
      <div className="container mt-5">
        <div className="row">
          <div className="col-lg-8 mt-5">
            <nav aria-label="breadcrumb">
              <ol className="breadcrumb bg-white">
                <li className="breadcrumb-item fw-bold">
                  <Link to="/">
                    <i className="fa fa-angle-double-left" /> Back to blog
                  </Link>
                </li>
              </ol>
            </nav>
            {loading ? (
              <MyBulletListLoader />
            ) : (
              <article>
                <Helmet>
                  <title>Dennis Mbugua - {blog.title}</title>
                  <meta name="description" content={blog.subtitle} />
                  <meta
                    name="keyword"
                    content="Dennis Mbugua, software engineer, technology expert, remote work specialist, software development, web development, software architecture, technical solutions, problem-solving, project management, team leadership, coding, programming, software optimization, software testing, software deployment, web applications, mobile applications, front-end development, back-end development, full-stack development, database management, API integration, software consulting, software maintenance, software security, software scalability, software performance, software frameworks, software tools, software engineer portfolio, software engineer resume, software engineer blog, software engineer projects, software engineer experience."
                  />
                  <meta property="og:image" content={blog.image} />
                </Helmet>
                <header className="mb-4">
                  <h1 className="fw-bolder mb-1">
                    {capitalLetter(blog.title)}
                  </h1>
                </header>
                <p className="post-meta">
                  <i className="fa fa-calendar-days"></i>{" "}
                  {getTime(blog.updated)}
                </p>
                <figure className="mb-4">
                  <img
                    className="img-fluid rounded w-100"
                    src={blog.image}
                    alt={blog.title}
                    style={{
                      height: "600px",
                      width: "500px",
                      borderRadius: "10px",
                    }}
                  />
                </figure>
                <section className="mb-5">
                  {/* Blog Details */}
                  <p className="fs-5" dangerouslySetInnerHTML={createBlog()} />

                  {/* Social Media Share */}
                  <div className="text-center mt-5 mb-5">
                    <h5 className="fw-bold">
                      If you read this far, share with the world to show that
                      you care
                    </h5>

                    <TwitterShareButton
                      url={shareUrl}
                      title={blog.subtitle}
                      via={"dennismbugua_"}
                      className="mr-2"
                    >
                      <TwitterIcon size={40} round={true} />
                    </TwitterShareButton>

                    <EmailShareButton
                      subject="IMPORTANT AND URGENT!"
                      url={shareUrl}
                      body="This is one of the most awesome article I've read today"
                      className="mr-2"
                    >
                      <EmailIcon size={40} round={true} />
                    </EmailShareButton>

                    <WhatsappShareButton
                      url={shareUrl}
                      title={blog.subtitle}
                      separator={" "}
                      className="mr-2"
                    >
                      <WhatsappIcon size={40} round={true} />
                    </WhatsappShareButton>

                    <RedditShareButton
                      url={shareUrl}
                      title={blog.subtitle}
                      className="mr-2"
                    >
                      <RedditIcon size={40} round={true} />
                    </RedditShareButton>

                    <TelegramShareButton
                      url={shareUrl}
                      title={blog.subtitle}
                      className="mr-2"
                    >
                      <TelegramIcon size={40} round={true} />
                    </TelegramShareButton>
                  </div>

                  {/* About Me */}
                  <div
                    className="mt-5 card-body"
                    style={{ borderRadius: "10px", backgroundColor: "#7B3F00" }}
                  >
                    <div class="col-md-2 col-xs-12 mx-auto text-center">
                      <img
                        width="140"
                        height="140"
                        class="rounded-circle me-3"
                        src={Me}
                        alt="Dennis Mbugua software engineer"
                      />
                    </div>
                    <div class="col-md-12 col-xs-12 text-white fw-bold mt-3">
                      Hello and thank you for visiting. My name is{" "}
                      <Link
                        to="/me"
                        onClick={scrollToTop}
                        className="text-decoration-underline"
                      >
                        Dennis Mbugua
                      </Link>
                      . Am a software engineer and I enjoy creative works using
                      open source software, serving people and making a
                      difference through code and design. <br />
                      <br />
                      Here is where I share what I have learnt, document notes
                      for myself and anything else I want to show the world. My
                      site is and will always be free from sponsored posts, ads,
                      or affliates in any form.
                      <hr />
                    </div>
                  </div>
                </section>
              </article>
            )}
          </div>

          <div className="col-lg-4">
            <div className="position-sticky" style={{ top: "6rem" }}>
              {/* FeaturedPost */}
              <div className="card">
                <div
                  style={{
                    borderRadius: "10px",
                    padding: "10px",
                    border: "2px",
                  }}
                >
                  <div className="card-header text-uppercase fw-bold text-center">
                    Featured Article
                  </div>
                  <div className="card-body" style={{ borderRadius: "10px" }}>
                    <p>
                      {loading ? (
                        <MyBulletListLoader />
                      ) : (
                        <Link
                          to={`/${featuredBlog.slug}`}
                          onClick={scrollToTop}
                          className="text-decoration-none"
                        >
                          {featuredBlog.title}
                        </Link>
                      )}
                    </p>
                    <hr className="text-dark" />
                  </div>
                </div>
              </div>

              {/* Recent Articles */}
              <div className="card mt-5">
                <div className="card-header text-uppercase fw-bold text-center">
                  Recent Article
                </div>
                <RecentArticles />
              </div>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
