import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import { BulletList } from "react-content-loader";
import BlogAPI from "../../API/BlogAPI";

export default function BlogFeeds() {
  // SCROLL TO TOP
  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  const MyBulletListLoader = () => <BulletList />;

  const [blogs, setBlogs] = useState([]);
  const [filter, setFilter] = useState("");
  const [loading, setLoading] = useState(true);
  const [blogsPerPage] = useState(10); // Number of blogs to load initially
  const [blogsPerScroll] = useState(5); // Number of blogs to load per scroll
  const [currentPage, setCurrentPage] = useState(1);
  const [totalBlogs, setTotalBlogs] = useState(0);

  const searchText = (event) => {
    setFilter(event.target.value);
  };

  const dataSearch = blogs.filter((blogPost) => {
    return Object.keys(blogPost).some((key) =>
      blogPost[key]
        .toString()
        .toLowerCase()
        .includes(filter.toString().toLowerCase())
    );
  });

  const capitalLetter = (word) => {
    return word ? `${word.charAt(0).toUpperCase()}${word.slice(1)}` : "";
  };

  const fetchBlogs = async () => {
    try {
      const startIndex = (currentPage - 1) * blogsPerPage;
      const res = await BlogAPI.get(`/api/blog`, {
        params: { start: startIndex, limit: blogsPerPage },
      });
      setBlogs((prevBlogs) => [...prevBlogs, ...res.data]);
      setTotalBlogs(res.headers["x-total-count"]);
    } catch (err) {
      // Handle error
    }
  };

  const getBlogs = () => {
    return dataSearch.map((blogPost, i) => (
      <React.Fragment key={i}>
        <div className="col">
          <div className="post-preview border-2 border-bottom border-primary">
            {loading ? (
              <MyBulletListLoader />
            ) : (
              <Link
                to={`/${blogPost.slug}`}
                onClick={scrollToTop}
                className="text-decoration-none"
              >
                <h3 className="post-title">{capitalLetter(blogPost.title)}</h3>
                <h4 className="post-subtitle">
                  {capitalLetter(blogPost.subtitle)}
                </h4>
                <p>{blogPost.readTime} Min Read</p>
              </Link>
            )}
          </div>
        </div>
      </React.Fragment>
    ));
  };

  const loadMoreBlogs = () => {
    if ((currentPage * blogsPerPage) < totalBlogs) {
      setCurrentPage((prevPage) => prevPage + 1);
    }
  };

  useEffect(() => {
    // Loader
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2500);

    fetchBlogs();
  }, [currentPage, blogsPerPage]);

  useEffect(() => {
    const handleScroll = () => {
      if (
        window.innerHeight + window.scrollY >= document.body.offsetHeight - 100
      ) {
        loadMoreBlogs();
      }
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <>
      <Helmet>
        {/* Helmet content */}
      </Helmet>

      <div className="container" style={{ marginTop: "100px" }}>
        {/* Search component */}
      </div>

      <div className="container px-4 px-lg-5 mb-5">
        <div className="row gx-4 gx-lg-5 justify-content-center">
          <div className="col-md-10 col-lg-8 col-xl-9">{getBlogs()}</div>
        </div>
      </div>
    </>
  );
}
