import React, { useState, useEffect } from "react";
import { Helmet } from "react-helmet";
import { Link, useParams } from "react-router-dom";
import Me from "./me.jpeg";
import { BulletList } from "react-content-loader";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import {
  TelegramShareButton,
  TelegramIcon,
  TwitterShareButton,
  TwitterIcon,
  EmailShareButton,
  EmailIcon,
  WhatsappIcon,
  WhatsappShareButton,
} from "react-share";
import BlogAPI from "../../API/BlogAPI";
import RecentArticles from "./recentArticles";
import NotFound from "../notFound/NotFound";

export default function BlogDetails() {
  // SCROLL TO TOP
  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  const MyBulletListLoader = () => <BulletList />;

  const [error, setError] = useState(false);

  const [blog, setBlog] = useState({});
  const { slug } = useParams();

  const [featuredBlog, setFeaturedBlog] = useState([]);

  const [loading, setLoading] = useState(true);

  const copied = () => toast("👏 Copied to Clipboard!");
  const print = () => toast("Send to print!");

  const handleCopyClipBoard = () => {
    setOpen(true);
    navigator.clipboard.writeText(window.location.toString());
  };

  const componentRef = useRef();
  const handlePrint = useReactToPrint({
    content: () => componentRef.current,
  });

  useEffect(() => {
    // Loader
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1000);

    // FeaturedAticle
    const fetchData = async () => {
      try {
        const res = await BlogAPI.get(`/api/blog/featured`);
        // If there are no featured posts
        if (res.data && res.data.length > 0) {
          setFeaturedBlog(res.data[0]);
        }
      } catch (err) {}
    };
    fetchData();

    // BlogPost
    const fetchBlog = async () => {
      try {
        const response = await BlogAPI.get(`/api/blog/${slug}`); // <-- passed to API URL
        setBlog(response.data);
      } catch (err) {
        if (err.response && err.response.status === 404) {
          setError(true);
        } else {
          // Handle other errors, such as network issues
          // console.error(err);
        }
      }
    };
    fetchBlog();
  }, [slug]);

  const createBlog = () => {
    return { __html: blog.body };
  };
  const capitalLetter = (word) => {
    if (word) return word.charAt(0).toUpperCase() + word.slice(1);
    return "";
  };

  const shareUrl = `https://www.dennismbugua.co.ke/${slug}`;
  // const shareUrl = window.location.href;

  let getTime = (blog) => {
    const options = { year: "numeric", month: "long", day: "numeric" };
    return new Date(blog).toLocaleDateString("en", options);
  };

  return (
    <>
      {error ? (
        <NotFound />
      ) : (
        <div className="container mt-5">
          <div className="row">
            <div className="col-lg-8 mt-5">
              <nav aria-label="breadcrumb">
                <ol className="breadcrumb bg-white">
                  <li className="breadcrumb-item fw-bold">
                    <Link to="/articles">
                      <i className="fa fa-angle-double-left" /> Back to blog
                    </Link>
                  </li>
                </ol>
              </nav>
              {loading ? (
                <MyBulletListLoader />
              ) : (
                <article>
                  <Helmet>
                    <title>Dennis Mbugua - {blog.title}</title>
                    <meta name="description" content={blog.subtitle} />
                    <meta
                      name="keyword"
                      content="Dennis Mbugua, software engineer, technology expert, remote work specialist, software development, web development, software architecture, technical solutions, problem-solving, project management, team leadership, coding, programming, software optimization, software testing, software deployment, web applications, mobile applications, front-end development, back-end development, full-stack development, database management, API integration, software consulting, software maintenance, software security, software scalability, software performance, software frameworks, software tools, software engineer portfolio, software engineer resume, software engineer blog, software engineer projects, software engineer experience."
                    />
                    <meta property="og:image" content={blog.image} />
                  </Helmet>
                  <header className="mb-4">
                    <h1 className="fw-bolder mb-1">
                      {capitalLetter(blog.title)}
                    </h1>
                  </header>
                  <p className="post-meta">
                    <i className="fa fa-calendar-days"></i>{" "}
                    {getTime(blog.updated)}
                  </p>
                  <figure className="mb-4">
                    <img
                      className="img-fluid rounded w-100"
                      src={blog.image}
                      alt={blog.title}
                      style={{
                        height: "600px",
                        width: "500px",
                        borderRadius: "10px",
                      }}
                    />
                  </figure>
                  <section className="mb-5">
                    {/* Blog Details */}
                    <p
                      className="fs-5"
                      dangerouslySetInnerHTML={createBlog()}
                    />

                    {/* Social Media Share */}
                    <div className="text-center mt-5 mb-5">
                      <h5 className="fw-bold">
                        Share the love with your friends!
                      </h5>

                      <TwitterShareButton
                        url={shareUrl}
                        title={blog.subtitle}
                        via={"dennismbugua_"}
                        className="me-3"
                      >
                        <TwitterIcon size={40} round={true} />
                      </TwitterShareButton>

                      <EmailShareButton
                        subject="IMPORTANT AND URGENT!"
                        url={shareUrl}
                        body="This is one of the most awesome article I've read today"
                        className="me-3"
                      >
                        <EmailIcon size={40} round={true} />
                      </EmailShareButton>

                      <WhatsappShareButton
                        url={shareUrl}
                        title={blog.subtitle}
                        separator={" "}
                        className="me-3"
                      >
