import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import { BulletList } from "react-content-loader";
import BlogAPI from "../../API/BlogAPI";

export default function BlogFeeds() {
  // SCROLL TO TOP
  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  const MyBulletListLoader = () => <BulletList />;

  const [blogs, setBlogs] = useState([]);
  const [filter, setFilter] = useState("");
  const [loading, setLoading] = useState(true);

  const searchText = (event) => {
    setFilter(event.target.value);
  };

  const dataSearch = blogs.filter((blogPost) => {
    return Object.keys(blogPost).some((key) =>
      blogPost[key]
        .toString()
        .toLowerCase()
        .includes(filter.toString().toLowerCase())
    );
  });

  const capitalLetter = (word) => {
    return word ? `${word.charAt(0).toUpperCase()}${word.slice(1)}` : "";
  };

  useEffect(() => {
    // Loader
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2500);

    const fetchBlogs = async () => {
      try {
        const res = await BlogAPI.get(`/api/blog`);
        setBlogs(res.data);
      } catch (err) {
        // Handle error
      }
    };
    fetchBlogs();
  }, []);

  const getBlogs = () => {
    return dataSearch.map((blogPost, i) => (
      <React.Fragment key={i}>
        <div className="col">
          <div className="post-preview">
            {loading ? (
              <MyBulletListLoader />
            ) : (
              <Link
                to={`/${blogPost.slug}`}
                onClick={scrollToTop}
                className="text-decoration-none"
              >
                <h3 className="post-title">{capitalLetter(blogPost.title)}</h3>
                <h4 className="post-subtitle">
                  {capitalLetter(blogPost.subtitle)}
                </h4>
                <p>{blogPost.readTime} Min Read</p>
              </Link>
            )}
          </div>
        </div>
      </React.Fragment>
    ));
  };

  return (
    <>
      <Helmet>
        <title>Dennis Mbugua - Articles</title>
        <meta
          name="description"
          content="Elevate your digital journey with insights from a Lead Software Engineer. Discover the power of innovative solutions and unlock your business's true potential. Let's connect and create something extraordinary together."
        />
        <meta
          name="keyword"
          content="Dennis Mbugua, software engineer, technology expert, remote work specialist, software development, web development, software architecture, technical solutions, problem-solving, project management, team leadership, coding, programming, software optimization, software testing, software deployment, web applications, mobile applications, front-end development, back-end development, full-stack development, database management, API integration, software consulting, software maintenance, software security, software scalability, software performance, software frameworks, software tools, software engineer portfolio, software engineer resume, software engineer blog, software engineer projects, software engineer experience."
        />
        <meta
          property="og:image"
          content="https://ahrefs.com/blog/wp-content/uploads/2019/12/fb-how-to-become-an-seo-expert.png"
        />
      </Helmet>

      <div className="container" style={{ marginTop: "100px" }}>
        <div className="row height d-flex justify-content-center align-items-center">
          <div className="col-md-8">
            <div className="search">
              <i className="fa fa-search"></i>
              <input
                type="text"
                className="form-control"
                value={filter}
                onChange={searchText.bind(this)}
                placeholder="Search an article"
              />
            </div>
          </div>
        </div>
      </div>

      <div className="container px-4 px-lg-5 mb-5">
        <div className="row gx-4 gx-lg-5 justify-content-center">
        <div className="col-md-10 col-lg-8 col-xl-9">
        <hr className="col-lg-10 my-4" />
          {getBlogs()}
          <hr className="col-lg-10 my-4" />
          </div>
        </div>
      </div>
    </>
  );
}
