import React, {useState} from "react";
import { Link } from "react-router-dom";
import { Helmet } from "react-helmet";
import {
  TwitterShareButton,
  TwitterIcon,
  EmailShareButton,
  EmailIcon,
  WhatsappShareButton,
  WhatsappIcon,
  RedditShareButton,
  RedditIcon,
  TelegramShareButton,
  TelegramIcon,
} from "react-share";
// import Me from "../assets/images/dennis_mbugua.png";
import RecentArticles from "../components/RecentArticles";
import MyBulletListLoader from "../components/MyBulletListLoader";
// import { capitalLetter, getTime } from "../utils/helpers";
// import useFetch from "../hooks/useFetch";

function BlogDetails(props) {
  const { match } = props;
  const { slug } = match.params;
  const [featuredBlog, setFeaturedBlog] = useState([]);

  const { data: blog, loading } = useFetch(`/api/blogs/${slug}`);
  const shareUrl = window.location.href;

  const createBlog = () => {
    return { __html: blog.body };
  };

  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  return (
    <>
      {loading && <MyBulletListLoader />}
      {!loading && blog && (
        <div className="container my-5">
          <div className="row">
            <div className="col-lg-8">
              <nav aria-label="breadcrumb">
                <ol className="breadcrumb bg-white">
                  <li className="breadcrumb-item fw-bold">
                    <Link to="/">
                      <i className="fa fa-angle-double-left" /> Back to blog
                    </Link>
                  </li>
                </ol>
              </nav>
              <article>
                <Helmet>
                  <title>Dennis Mbugua - {blog.title}</title>
                  <meta name="description" content={blog.subtitle} />
                  <meta
                    name="keyword"
                    content="Dennis Mbugua, software engineer, technology expert, remote work specialist, software development, web development, software architecture, technical solutions, problem-solving, project management, team leadership, coding, programming, software optimization, software testing, software deployment, web applications, mobile applications, front-end development, back-end development, full-stack development, database management, API integration, software consulting, software maintenance, software security, software scalability, software performance, software frameworks, software tools, software engineer portfolio, software engineer resume, software engineer blog, software engineer projects, software engineer experience."
                  />
                  <meta property="og:image" content={blog.image} />
                </Helmet>
                <header className="mb-4">
                  <h1 className="fw-bolder mb-1">
                    {capitalLetter(blog.title)}
                  </h1>
                </header>
                <p className="post-meta">
                  <i className="fa fa-calendar-days"></i> {getTime(blog.updated)}
                </p>
                <figure className="mb-4">
                  <img
                    className="img-fluid rounded w-100"
                    src={blog.image}
                    alt={blog.title}
                    style={{
                      height: "600px",
                      width: "500px",
                      borderRadius: "10px",
                    }}
                  />
                </figure>
                <section className="mb-5">
                  {/* Blog Details */}
                  <p className="fs-5" dangerouslySetInnerHTML={createBlog()} />

                  {/* Social Media Share */}
                  <div className="text-center mt-5 mb-5">
                    <h5 className="fw-bold">Share this article</h5>
                    <TwitterShareButton url={shareUrl} title={blog.title}>
                      <TwitterIcon size={32} round className="me-2" />
                    </TwitterShareButton>
                    <EmailShareButton url={shareUrl} subject={blog.title}>
                      <EmailIcon size={32} round className="me-2" />
                    </EmailShareButton>
                    <WhatsappShareButton url={shareUrl} title={blog.title}>
                      <WhatsappIcon size={32} round className="me-2" />
                    </WhatsappShareButton>
                    <RedditShareButton url={shareUrl} title={blog.title}>
                      <RedditIcon size={32} round className="me-2" />
                    </RedditShareButton>
                    <TelegramShareButton url={shareUrl} title={blog.title}>
                      <TelegramIcon size={32} round className="me-2" />
                    </TelegramShareButton>
                  </div>
                </section>
              </article>
            </div>

            <div className="col-lg-4">
              <div className="position-sticky" style={{ top: "6rem" }}>
                {/* FeaturedPost */}
                <div className="card">
                  <div
                    style={{
                      borderRadius: "10px",
                      padding: "10px",
                      border: "2px",
                    }}
                  >
                    <div className="card-header text-uppercase fw-bold text-center">
                      Featured Article
                    </div>
                    <div className="card-body" style={{ borderRadius: "10px" }}>
                      <p>
                        {loading ? (
                          <MyBulletListLoader />
                        ) : featuredBlog.length === 0 ? (
                          <p>Stay tuned for my upcoming featured articles!</p>
                        ) : (
                          <Link
                            to={`/${featuredBlog.slug}`}
                            onClick={scrollToTop}
                            className="text-decoration-underline"
                          >
                            {featuredBlog.title}
                          </Link>
                        )}
                      </p>
                      <hr className="text-dark" />
                    </div>
                  </div>
                </div>

                {/* Recent Articles */}
                <div className="card mt-5">
                  <div className="card-header text-uppercase fw-bold text-center">
                    Recent Article
                  </div>
                  <RecentArticles />
                </div>
              </div>
            </div>
          </div>
        </div>
      )}
    </>
  );
}

export default BlogDetails;
