import React, { useState, useEffect, useRef, Fragment } from 'react'
import emailjs from '@emailjs/browser';
import { useNavigate } from "react-router-dom";
import { ReactTitle } from 'react-meta-tags';

export default function HireMe() {

    // Contact Form
    const navigate = useNavigate();

    const form = useRef();

    const sendEmail = (e) => {
        e.preventDefault();

        emailjs
            .sendForm(
                "service_pwvmojf",
                "template_ygg6k2c",
                form.current,
                "user_qGuGQyWW4uKSaHcStVUxO"
            )
            .then(
                (result) => {
                    console.log(result.text);
                },
                (error) => {
                    console.log(error.text);
                }
            );
        e.target.reset();

        navigate('/thank-you');
    };



    return (
        <>
            <div className="container my-5">
                <ReactTitle title='The best thing for you is me! 😇' />
                <div className="row justify-content-center">
                    <div className="col-lg-9">
                        <h1 className='mt-5 text-center fw-bold'>The Best Thing For You is Me! &#128522;</h1>
                        <hr />
                        <h3 className="mb-3">With me you’re not going to get an off-the-shelf, run-of-the mill type of project and results.
                            I work with you & your team to understand the direction the company is heading with steadfast
                            readiness to offer sincere service. I have a strong pristine focus, in my pursuit in ensuring I
                            produce marvelous and exceptional work &#128640; with a lot of love &#10084;&#65039; for my clients.</h3>
                        <hr />
                        <h3 className="text-center">Am the guy to contact when you're tired of playing small </h3>
                        <h1 className='fw-bold mt-3 text-center'>Contact Me &#128231;</h1>
                        <hr className='mb-5' />
                        <form ref={form} onSubmit={sendEmail}>
                            <div className="row g-3">
                                <div className="col-md-6 text-center">
                                    <label htmlFor="first-name" className="form-label">First Name</label>
                                    <input style={{ textAlign: 'center' }} type="text" className="form-control" id="firstname" name="firstname" placeholder='Your First Name' required />
                                </div>
                                <div className="col-md-6 text-center">
                                    <label htmlFor="last-name" className="form-label">Last Name</label>
                                    <input style={{ textAlign: 'center' }} type="text" className="form-control" id="lastname" name="lastname" placeholder='Your Last Name' required />
                                </div>
                                <div className="col-md-6 text-center">
                                    <label htmlFor="your-email" className="form-label">Your Email</label>
                                    <input style={{ textAlign: 'center' }} type="email" className="form-control" id="email" name="email" placeholder='Your Best Email' required />
                                </div>
                                <div className="col-md-6 text-center">
                                    <label htmlFor="your-subject" className="form-label">Subject</label>
                                    <input style={{ textAlign: 'center' }} type="text" className="form-control" id="subject" name="subject" placeholder='Your Subject Matter' />
                                </div>
                                <div className="col-12 text-center">
                                    <label htmlFor="your-message" className="form-label">Your Message</label>
                                    <textarea className="form-control" id="message" name="message" rows="5" placeholder='Write Your Message in Details' required></textarea>
                                </div>
                                <div className="col-12 mb-5">
                                    <div className="row">
                                        <div className="col-md-12">
                                            <button type="submit" className="btn btn-block w-100 h-100" style={{ backgroundColor: "#6E260E" }}><h5 className='fw-bold text-white'>Let's Do It! <i className="fa-solid fa-envelope-open"></i></h5></button>
                                        </div>
                                    </div>
                                </div>

                            </div>
                        </form>
                    </div>
                </div>
            </div>

        </>
    )
}



















