import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
// import Footer from "../footer/Footer";
import { BulletList } from "react-content-loader";
import BlogAPI from "../../API/BlogAPI";

export default function BlogFeeds() {
  // SCROLL TO TOP
  const scrollToTop = () => {
    window.scrollTo(0, 0);
  };

  const MyBulletListLoader = () => <BulletList />;

  const [blogs, setBlogs] = useState([]);
  const [filter, setFilter] = useState("");
  // const [visible, setVisible] = useState(3);

  const [loading, setLoading] = useState(true);

  const searchText = (event) => {
    setFilter(event.target.value);
  };
  const dataSearch = blogs.filter((blogPost) => {
    return Object.keys(blogPost).some((key) =>
      blogPost[key]
        .toString()
        .toLowerCase()
        .includes(filter.toString().toLowerCase())
    );
  });

  const capitalLetter = (word) => {
    if (word) return word.charAt(0).toUpperCase() + word.slice(1);
    return "";
  };

  useEffect(() => {
    // Loader
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2500);

    const fetchBlogs = async () => {
      try {
        const res = await BlogAPI.get(`/api/blog`)
        setBlogs(res.data)
      } catch (err) {}
    };
    fetchBlogs();
  }, []);

  const getBlogs = () => {
    let list = [];
    let result = [];

    dataSearch.map((blogPost, i) => {
      return list.push(
        <>
          <div className="col">
            <div className="post-preview">
              {loading ? (
                <MyBulletListLoader />
              ) : (
                <Link
                  to={`/${blogPost.slug}`}
                  onClick={scrollToTop}
                  className="text-decoration-none"
                >
                  <h3 className="post-title" key={i}>
                    {capitalLetter(blogPost.title)}
                  </h3>
                  <h4 className="post-subtitle" >
                    {capitalLetter(blogPost.subtitle)}
                  </h4>
                  <p>{blogPost.readTime} Min Read</p>
                </Link>
              )}
            </div>
          </div>
        </>
      );
    });

    for (let i = 0; i < list.length; i += 2) {
      result.push(
        <>
          <div key={i} className="row gx-4 gx-lg-5 justify-content-center">
            <div className="col-md-10 col-lg-8 col-xl-9">{list[i]}</div>
            <hr className="col-lg-10 my-4" />
            <div className="col-md-10 col-lg-8 col-xl-9">
              {list[i + 1] ? list[i + 1] : null}
            </div>
            <hr className="col-lg-10 my-4" />
          </div>
        </>
      );
    }
    return result;
  };

  return (
    <>
      <div className="container" style={{ marginTop: "100px" }}>
        <div className="row height d-flex justify-content-center align-items-center">
          <div className="col-md-8">
            <div className="search">
              <i className="fa fa-search"></i>
              <input
                type="text"
                className="form-control"
                value={filter}
                onChange={searchText.bind(this)}
                placeholder="Search an article"
              />
            </div>
          </div>
        </div>
      </div>

      {/* <Categories/> */}

      <div className="container px-4 px-lg-5 mb-5">{getBlogs()}</div>

    </>
  );
}

